mapper.select:
SELECT T1.TEAM_NO ,T1.TEAM_NM ,T1.TEAM_GENDER AS GENDER ,T1.TEAM_IMG_CD AS IMG_CD ,T2.IMG_FILE ,T1.TEAM_AREA AS AREA ,(SELECT S1.COMMON_CD FROM TB_C_COMMON S1 WHERE S1.COMMON_CD = T1.TEAM_AREA) AS AREA_NM FROM TB_B_TEAM T1 INNER JOIN TB_B_FILE T2 ON T1.TEAM_IMG_CD = T2.FILE_CD INNER JOIN (SELECT S2.TEAM_AREA ,S2.TEAM_GENDER FROM TB_B_TEAM S2 WHERE S2.TEAM_NO = #{id}) T3 ON T1.TEAM_AREA = T3.TEAM_AREA WHERE T1.TEAM_USE_YN = 'Y' AND T1.TEAM_GENDER <> T3.TEAM_GENDER
AND T1.TEAM_NM LIKE '%'||#{search}||'%'
ORDER BY T1.TEAM_REG_TIME DESC;

mapper.insert:
INSERT INTO TB_B_BOARD( BOARD_NO,
BOARD_UPPER,
TEAM_NO, BOARD_SEQ, BOARD_COMMENT, BOARD_REG_TIME ) VALUES ( #{boardNo},
#{boardUpper},
#{teamNo}, (SELECT COUNT(*) + 1 FROM TB_B_BOARD S1 WHERE S1.TEAM_NO = #{teamNo}
AND S1.BOARD_UPPER = #{boardUpper}
), #{boardComment}, NOW() )
